package com.company;public class Main {    public static void main(String[] args) {        int[][] arr ={                {1,3,1,5},                {2,2,4,1},                {5,0,2,3},                {0,6,1,2}        };        FindMaxGoldCollect(arr);    }    private static void FindMaxGoldCollect(int[][] arr) {        int[][] memTable = new int[arr.length][arr.length];        for(int j=0;j<arr.length;j++){            for(int i=0;i<arr.length;i++){                if(j==0){                    memTable[i][j]=arr[i][j];                }else if(i == 0){                   memTable[i][j] = Math.max(memTable[i][j-1],memTable[i+1][j-1])+arr[i][j];                }else if(i == arr.length - 1){                   memTable[i][j] = Math.max(memTable[i][j-1],memTable[i-1][j-1])+arr[i][j];                }else{                    memTable[i][j]=Math.max(memTable[i-1][j-1],Math.max(memTable[i][j-1],memTable[i+1][j-1]))+arr[i][j];                }            }        }        for (int[] ints : memTable) {            for (int j = 0; j < memTable.length; j++) {                System.out.print(ints[j] + " ");            }            System.out.println();        }    }}