package bd.edu.seu;import com.sun.scenario.effect.impl.sw.java.JSWBlend_SRC_OUTPeer;import java.util.*;public class Main {    static boolean isSubsetSum(int set[], int n, int sum)    {        boolean subset[][] = new boolean[sum+1][n+1];        for (int i = 0; i <= n; i++)            subset[0][i] = true;        for (int i = 1; i <= sum; i++)            subset[i][0] = false;        for (int i = 1; i <= sum; i++)        {            for (int j = 1; j <= n; j++)            {                subset[i][j] = subset[i][j-1];                if (i >= set[j-1])                    subset[i][j] = subset[i][j] ||                            subset[i - set[j-1]][j-1];            }        }        for(int i=0;i<=sum;i++){            for(int j=0;j<=n;j++){                System.out.print(subset[i][j]+" ");            }            System.out.println();        }        return subset[sum][n];    }    public static void main (String args[])    {        int set[] = {1,2,3,5,7};        int sum = 8;        int n = set.length;        if (isSubsetSum(set, n, sum) == true)            System.out.println("Found a subset with given sum");        else            System.out.println("No subset with given sum");    }}